import axios from "axios";
import React, { useEffect, useState } from "react";
import "./ContentOfFormation.scss";
import marked from 'marked'
const ContentOfFormation = (props) => {
  let { setTrigger, title , id } = props;

  const [content, setContent] = useState('')



  useEffect(() => {
    axios.get(`http://localhost:3000/markdown?file=${id}`).then((res) =>{
      if(!res.ok)
      setContent(res.data)
      
    })
  },[])


  console.log(id);

  // console.log(content);

  return (
    <div className="contentOfFormation-Body">
      <div className="title">
        <img
          onClick={() => props.setTrigger(false)}
          src="./img/mail/back.png"
          alt=""
        />
        <p>{title}</p>
      </div>
      <div className="bodyContent">
          <div dangerouslySetInnerHTML={{__html: marked(content)}}/>
      </div>
    </div>
  );
};

export default ContentOfFormation;
