import React from 'react';
import "./Task.scss";
const Task = (props) => {
    let { trigger, data } = props
    if (trigger == true) {
        console.log(data)
        return (
            <div className="task">
                
                <img onClick={() => props.setTrigger(false)} src="./img/mail/back.png" alt="" srcset=""/>
                <h1>Voici la liste des tâches à faire pour le {data[0].date.substr(0,10)}</h1>
                {data.map((task, i) => {     
                    return (
                        <div> <p className={task.finished == 1 ? 'finished' : null}> - {task.content}</p>
                        <img onClick={() => props.setTriggerSend(false)} src="./img/mail/bin.png" alt="" srcset=""/></div>
                       
                        ) 
                })}
            </div>
        );
    }
    else{
        return null
    }
   
};

export default Task;
