import React from 'react';
import "./Task.scss";
const Task = (props) => {
    let { trigger, data } = props
    if (trigger == true) {
        console.log(data)
        return (
            <div className="task">
                
                <img onClick={() => props.setTrigger(false)} src="./img/mail/back.png" alt="" srcset=""/>
                <h1>Voici la liste des tâches à faire pour le {data[0].date.substr(0,7)}</h1>
                {/* {data.map((task, i) => {     
                    return ({task}) 
                })} */}
            </div>
        );
    }
    else{
        return null
    }
   
};

export default Task;
