import React, { useState } from 'react';
import "./QuizzContent.scss";

const QuizzContent = (props) => {

    let { title } = props

    const [question, setQuestion] = useState([
        {
            "id": 1,
            question: "Pour vous un contrat d'assurance vie c'est :",
            res1: "L'autre nom pour une assurance de décès",
            res2: "Un produit d'épargne polyvalent",
            res3: "Un placement pour vieilles dames fortunées et qui suscite la  convoitise de proches mal intentionés",
        },
        {
            "id": 2,
            question: "Vous estimez que l'assurance-vie c'est :",
            res1: "Une façon de diversifier ses investissements au sein d'un seul placement",
            res2: "Contraignant car l'argent est toujours bloqué et il faut attendre huit ans pour pouvoir y participer",
            res3: "Un produit tunnel qui ne produit des effets qu'au moment de la retraite", 
        },
        {
            "id": 3,
            question: "La clause bénéficiaire d'un contrat c'est :",
            res1: "Un moyen pour l'assureur de garder les bénéfices tout en laissant à l'assuré ses yeux pour pleurer lorsqu'il y a des pertes sur son contrat",
            res2: "Une étape essentielle lorsque l'assuré veut transmettre les capitaux de son contrat",
            res3: "Une autre manière de faire un testament sans mettre ses proches dans la confidence",
        }
    ]);
    const [incrementation, setIncrementation] = useState(1);

    console.log(question);



    return (
        <div className="QuizzContent-Body">
            <div className="title">
                <img
                onClick={() => props.setTrigger(false)}
                src="./img/mail/back.png"
                alt=""
                />
                <p>{title}</p>
            </div>
            <div className="quizz">
                {question.
                filter((data) => data.id == incrementation)
                .ma
                }
            </div>
        </div>
    );
};

export default QuizzContent;
