import React, { useState } from 'react';
import "./QuizzContent.scss";

const QuizzContent = (props) => {

    let { title } = props

    const [incrementation, setIncrementation] = useState(1);
    const [question, setQuestion] = useState([
        {
            "id": 1,
            question: "Pour vous un contrat d'assurance vie c'est :",
            proposition: {
                res1: ["L'autre nom pour une assurance de décès", false],
                res2: ["Un produit d'épargne polyvalent", true ],
                res3: ["Un placement pour vieilles dames fortunées et qui suscite la  convoitise de proches mal intentionés", false],
            }
        },
        {
            "id": 2,
            question: "Vous estimez que l'assurance-vie c'est :",
            proposition: {
                res1: ["Une façon de diversifier ses investissements au sein d'un seul placement", true],
                res2: ["Contraignant car l'argent est toujours bloqué et il faut attendre huit ans pour pouvoir y participer", false],
                res3: ["Un produit tunnel qui ne produit des effets qu'au moment de la retraite", false ], 
            }
        },
        {
            "id": 3,
            question: "La clause bénéficiaire d'un contrat c'est :",
            proposition: {
                res1: ["Un moyen pour l'assureur de garder les bénéfices tout en laissant à l'assuré ses yeux pour pleurer lorsqu'il y a des pertes sur son contrat", false],
                res2: ["Une étape essentielle lorsque l'assuré veut transmettre les capitaux de son contrat", true],
                res3: ["Une autre manière de faire un testament sans mettre ses proches dans la confidence", false],
            }
        }
    ]);
    const increment = () => { 
        setIncrementation(incrementation + 1)
    }
    // console.log(question[incrementation].proposition);



    return (
        <div className="QuizzContent-Body">
            <div className="title">
                <img
                onClick={() => props.setTrigger(false)}
                src="./img/mail/back.png"
                alt=""
                />
                <p>{title}</p>
            </div>
            <div className="quizz">
                <div>
                    {question.
                    filter((data) => data.id == incrementation)
                    .map((data) => (
                        <p>{data.question}</p>
                    ))
                    } 
                    {question.
                    filter((data) => data.id == incrementation)
                    .map((data) => (
                        <p>{data.question}</p>

                    ))
                    }
                </div>
                <button onClick={() => increment()}>Valider</button>
            </div>
        </div>
    );
};

export default QuizzContent;
