import React from 'react';
import './Sidebar.scss'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    Redirect,
    useHistory,
    useLocation
  } from "react-router-dom";
import Auth from '../services/Auth';


const Sidebar = () => {
    let history = useHistory();
    let location = useLocation();

    let signout = () => {
        localStorage.clear();
        history.push('/login');
        // Auth.signout(() => {
        // history.push('/');
        // });
    };

    if(window.location.pathname === '/mail'){
        console.log('yes');
    }


    let mail = ""
    let home = ""
    let folder = ""
    let calendar = ""
    let visio = ""
    let formation = ""

    let profile = ""
    let setting = ""

    switch (true){
        case window.location.pathname === '/mail':
            mail = "/img/sidebar/message-icon-active.png";
            home = "/img/sidebar/home-icon.png"
            folder = "/img/sidebar/folder-icon.png"
            calendar = "/img/sidebar/calendar-icon.png"
            visio = "/img/sidebar/visio-icon.png"
            formation = "/img/sidebar/formation-icon.png"

            profile = "/img/sidebar/setting-icon.png"
            profile = "/img/sidebar/profile-icon.png"
            break;
        case window.location.pathname === '/formation':
            formation = "/img/sidebar/formation-icon-active.png";
            mail = "/img/sidebar/message-icon.png";
            home = "/img/sidebar/home-icon.png"
            folder = "/img/sidebar/folder-icon.png"
            calendar = "/img/sidebar/calendar-icon.png"
            visio = "/img/sidebar/visio-icon.png"
            profile = "/img/sidebar/profile-icon.png"
            break;
        default:
            home = "/img/sidebar/home-icon.png";
            break;
    }




 
    return (
        <div className="side-bar">
              <div>
                <img src={home} alt="" className="icon-sidebar"/>
                <img src={folder} alt="" className="icon-sidebar"/>
                <img src={calendar} alt="" className="icon-sidebar"/>
                <div>
                    <img src={mail}  alt="" className="icon-sidebar"/>
                    <span className="notification">   </span>
                </div>
                <img src={visio} alt="" className="icon-sidebar"/>
                <img src={formation}  alt="" className="icon-sidebar"/>
            </div>
            <div>
                <img src={profile} alt="" className="icon-sidebar" onClick={ signout }/>
                <img src={setting} alt="" className="icon-sidebar"/>
            </div>
        </div>
    );
};

export default Sidebar;
