import React from 'react';
import "./Show.scss";
const Task = (props) => {
    let { trigger, data } = props
    if (trigger == true) {
        console.log(data)
        return (
            <div>
                <img onClick={() => props.setTrigger(false)} src="./img/mail/back.png" alt="" srcset=""/>
                <p>{data.content}</p>
            </div>
        );
    }
    else{
        return null
    }
   
};

export default Task;
