import React from 'react';
import "./Show.scss";


const Show = (props) => {
    let { mail } = props
    

    useEffect(() => {
        if (ChangeURL != url) {
          setUrl(ChangeURL)
          setFetch(true)
        }
       
        if (fetch) {
            axios.get(`http://localhost:3000/user/${url}`).then(function (response) {
            setData(response.data)
            setFetch(false)
            setChange(false)
          })
        } 
        // console.log(data);
      }, [data,ChangeURL,fetch, url, projet])


    return (
        <div className="show">
            <div>
                <ul>
                    <div>
                        <img src="./img/mail/back.png" alt="" srcset=""/>
                       
                    </div>
                    <div>
                        <img src="./img/mail/bin.png" alt="" srcset=""/>
                        <img src="./img/mail/move.png" alt="" srcset=""/>
                        <img src="./img/mail/print.png" alt="" srcset=""/>
                        <img src="./img/mail/read.png" alt="" srcset=""/>
                        <img src="./img/mail/arch.png" alt="" srcset=""/>
                    </div>
                </ul>
            </div>
            <div id="title">
                <div>
                    <img src="./img/mail/bin.png" alt="" srcset=""/>
                </div>
                <div>
                    <div>
                        <h1>{mail.title} (Objet du mail)</h1>
                        <ul>
                        <img src="./img/mail/bin.png" alt="" srcset=""/>
                        <img src="./img/mail/move.png" alt="" srcset=""/>
                        <img src="./img/mail/print.png" alt="" srcset=""/>
                    </ul>
                    </div>
                    
                    {/* <p> {mail.content}</p> */}
                   
                </div>
                
            </div>
        </div>
    );
};

export default Show;
