import React from 'react';
import "../components/Register/Register.scss";


import {
    useHistory,
    useLocation
  } from "react-router-dom";
import Auth from '../services/Auth';
const axios = require('axios').default;


// getter
// localStorage.getItem('myData');

const Register = () => {
    const [email, setEmail] = React.useState("");
    const [emailConfi, setEmailConfi] = React.useState("");
    const [password, setPassword] = React.useState("");
    const [error, setError] = React.useState("");
    
    let history = useHistory();
    let location = useLocation();

    let { from } = location.state || { from: { pathname: "/" } };


    
    let register = (event) => {
        event.preventDefault()
        axios.get(`http://localhost:3000/user?email=${email}&password=${password}`).then(function (response) {
            console.log(response.data.status )
            if (response.data.status !== "404") {
                console.log(response)
                localStorage.setItem('email', response.data.data[0].email);
                Auth.signin(() => {
                history.push('/mail');
                });
            }
            else{
                setError("Veillez renseinger un email et un mot de passe valide")
            }
        })
    };


    return (
      <div id="Register">        
        <form className="test" onSubmit={register}>
          
          <div>
            <h1>Inscription</h1>
          </div>
          
        <div>
          <div>
            <label>Adresse email professionnelle: </label>
            <input
              name="email"
              type="email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required />
          </div>
       
          <div>
            <label>Confirmation de l'adresse email </label>
            <input
              name="emailConfi"
              type="email"
              value={email}
              onChange={e => setEmailConfi(e.target.value)}
              required />
          </div>
        
          <div> 
            <label>Mot de passe:</label>
            <input
              name="password"
              type="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required />
             
              
          </div>

          <div> 
            <label>Numéro de téléphone (facultatif)</label>
            <input
              name="tel"
              type="text"
              value={password}
              onChange={e => setTel(e.target.value)}
              required />
            
              
          </div>
        </div>
      
        <div>
          <button>Se connecter</button>
        </div>
        
        <p>{error}</p>
      </form>
    </div>

  );
};

export default Register;
