import React, { useEffect, useState } from "react";
import './sendEmail.scss'
const axios = require('axios').default;


const SendEmail = (props) => {

  let { triggerSend, setTriggerSend } = props
  const [receiver, setReceiver] = React.useState("");
  const [projet, setProjet] = React.useState("");
  const [content, setContent] = React.useState("");

  let send = (event) => {
    event.preventDefault()
    axios.get(`http://localhost:3000/user?receiver=${email}&sender=${password}`).then(function (response) {
        if (response.data.status !== "404") {
            localStorage.setItem('email', response.data.data[0].email);
            Auth.signin(() => {
            history.push('/mail');
            });
        }
        else{
            setError("Veillez renseinger un email et un mot de passe valide")
        }
    })
};


  if (triggerSend == true) {
    return (
        <div class = "sendEmail">
          <img onClick={() => props.setTriggerSend(false)} src="./img/mail/bin.png" alt="" srcset=""/>
          <div>
            <div>
              <p>A</p>
              <input
              name="receiver"
              type="text"
              value={receiver}
              onChange={e => setReceiver(e.target.value)}
              required />
              <p>CC/Cci</p>
            </div>
            <div>
              <p>Projet</p>
              <input
              name="projet"
              type="text"
              value={projet}
              onChange={e => setProjet(e.target.value)}
              required />
            </div>
            <div>
              <textarea  name="content"
              type="text"
              value={content}
              onChange={e => setContent(e.target.value)}
              required  cols="30" rows="10" placeholder="Ecrire"></textarea>
            </div>
            <div>
              <button>Envoyer</button>
              <img src="./img/mail/bin.png" alt="" srcset=""/>
              <img src="./img/mail/bin.png" alt="" srcset=""/>
              <img src="./img/mail/bin.png" alt="" srcset=""/>
            </div>
          </div>
        </div>
      );
    }
    else{
        return null
    }
  }
  
  {/* <span onClick={() => props.setTriggerSend(false)}>Ecrire un mail</span> */}


export default SendEmail;
