import React, { useState } from 'react';
import "./Cards.scss";
import ContentOfFormation from './ContentOfFormation';

const Cards = (props) => {
    const {data} = props


    const [trigger, setTrigger] = useState([false])

    const triggerSetting = (arg) => {
        if(trigger == true) return setTrigger(arg)
        if(filterData == arg) return setFilterData('')
    }




    return (
        <div className="card" data-type={data.type}>
            <p>{data.title}</p>
            <p>{data.content}</p>
            <p>Temps nécessaire : environ {data.time}</p>
            <button className="btn-card"  data-type={data.type} onClick={() => triggerSetting()}>Accéder à la formation</button>

            {trigger ? 
                <ContentOfFormation />
                : null
            }

        </div>
    );
};

export default Cards;
