import React, { useEffect, useState }from 'react';
import "./Quizz.scss";
import marked from 'marked'
const Quizz = (props) => {

    let { type, id} = props

    const [content, setContent] = useState('')
    const [trigger, setTrigger] = useState(false)


    useEffect(() => {
        fetch(`http://localhost:3000/markdown?file=${id}`).then((res) =>{
        if (!res.ok) {
            console.log('Not Found')
        }
        res.text().then((text) => setContent(text))
        })
    },[])













    return (
        <div>
            <div className="Body-Quizz" data-type={type}>
                <div dangerouslySetInnerHTML={{__html: marked(content)}}/>
                <button>Je valide la formation</button>
            </div>
            <div>
            {trigger ?
                    <div className="popUp">
                        <ContentOfFormation setTrigger={setTrigger} title={titleFormation} id={id} type={type}/>
                    </div>
                : null
            }
            </div>
        </div>
    );
};

export default Quizz;
